import styled from "styled-components";

export const InfoContainer = styled.div` 
    color : #fff;
    ${'' /* background: ${({lightBg }) => (lightBg ? '#f9f9f9' : '010606')}; */}

    @media screen and (max-width : 768px){
        padding : 100px 0;
    }

`

export const InfoWrapper = styled.div` 
    display : grid;
    z-index: 1;
    height : 860px;
    width :100%;
    max-width : 1100px;
    margin-right : auto;
    margin-left : auto;
    padding : 0 24px;
    justify-content : center;
`

export const InfoRow = styled.div` 
    display : grid;
    grid-auto-colums : minmax(auto,1fr);
    align-items : center;
    ${'' /* grid-templete-areas : ${({ImgStart}) => (ImgStart ? 'col2 col1' : 'col1 col2')}; */}

    @media screen and (max-width : 768px){
        grid-templete-areas : ${({ImgStart})=>(ImgStart ? 'col1 col2' : `'col1 col1' 'col2 col2`)};
    }

`

export const Colum1 = styled.div`
    margin-bottom : 15px;
    padding : 0 15px;
    grid-area : col1;
` 
export const Colum2 = styled.div`
    margin-bottom : 15px;
    padding : 0 15px;
    grid-area : col2;
` 

export const TextWrapper = styled.div` 
    max-width : 540px;
    padding-top : 0 ;
    padding-bottom : 60px;
`

export const TopLine = styled.p` 
    color : #01bf71;
    font-size : 16px;
    line-height : 16px;
    font-weight : 700;
    letter-spacing : 1.4px;
    text-transform : uppercase;
    margin-bottom : 16px;
`
export const Heading = styled.h1`
    margin-bottom : 24px;
    font-size:  48px;
    line-height: 1.1px;
    font-weight: 600;
    color : ${({ligthText})=>(ligthText ? '#f7f8fa' : '010606')};

    @media screen and (max-width : 480px){
        font-size : 32px; 

    } 

`

export const Subtitle = styled.p`
    max-width : 440px;
    margin-botton : 35px;
    font-size : 18px;
    line-height: 24px;
    color : ${({darkText}) => (darkText ? '010606' : '#fff' )};
`

export const BtnWrap = styled.div`
    display : flex;
    justify-content: center;
` 
export const ImgWrap = styled.div`
    max-width : 555px;
    height : 100%;
` 

export const Img = styled.img`
    width : 100%;
    margin : 0 0 10px 0;
    padding-right :  0;
`
