import styled from 'styled-components';

export const SigninContainer = styled.div` 
    background : #01fb71;
    display : grid;
    height : 100vh;
    justify-content : center;
    grid-auto-columns : minmax(auto,1fr);
    grid-template-areas : 'col1 col2';

    @media screen and (max-width : 768px){
        grid-template-areas : 
        'col1 col1'
        'col2 col2';
    }

`

export const SigninFormContainer = styled.div` 
    grid-area : col1;

`

export const Form = styled.form` 
    background : #010606;
    justify-content : center;
    margin : 0 auto;
    height : auto;
    display : grid;
`

export const FormLabels = styled.label` 
    color: #fff;
    font-size : 14px;
`

export const FormInputs = styled.input`
    padding : 16px 16px;
    border : none;
    border-radius : 4px;
`

export const SigninInfo = styled.div` 
    background : #fff;
    width: 100%;
    height: 100%;
    display: grid;
    justify-content: center;
    align-items: center;
    grid-area : col2;

`

export const InfoWrapper = styled.div` 
    margin-bottom : 15px;
    padding : 0 30px;

`

export const Img = styled.img ` 

`

export const SignInfoH1 = styled.h1` 
    margin-bottom : 24px;
    font-size:  40px;
    line-height: 1.1;
    max-width : 600px;
    font-weight: 600;
`

export const SigninInfoP = styled.p` 
    max-width : 440px;
    margin : 0 0 25px 0;
    font-size : 18px;
    line-height: 23px;
`
export const SignTitle = styled.p` 
    color : #01bf71;
    font-weight: 600;
    margin-bottom : 16px;
    text-transform: uppercase;

`

